import {Container, AppBar, Typography, Grow, Grid} from '@material-ui/core';

import {Provider, useDispatch} from "react-redux";

import {getSets} from "./actions/sets";
import {getPosts} from "./actions/posts";

import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import Sets from './components/Sets/Sets';

import memories from './images/memories.png';
import useStyles from './styles';

//for multipage
import LearnSS from "./LearnSS"; //sample for extra page
import Home from "./Home"; //sample for extra page

import {Route,Link, Router, BrowserRouter, Routes} from "react-router-dom";

const App = () => {
    const  classes = useStyles();
    const dispatch = useDispatch();

    useEffect(()=> {
        dispatch(getPosts());
        dispatch(getSets());
    },[dispatch]);

    return (
<Container maxidth={"lg"}>

 <BrowserRouter>
     <Routes>
         <Route r={useEffect()} path="/">
             <Home/>
         </Route>
     </Routes>
 </BrowserRouter>

    {/*<AppBar className={classes.appBar} position = "static" color ="inherit">*/}
    {/*    <Typography className={classes.heading} variant="h2" aligin="center">StudyStream</Typography>*/}
    {/*    <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>*/}
    {/*</AppBar>*/}

    }
</Container>

);
}
export default App;
