import React, {useEffect} from 'react';
import ReactDOM from 'react-dom';
import {Provider, useDispatch} from 'react-redux';
import {createStore,applyMiddleware,compose} from 'redux';
import thunk from 'redux-thunk';
import reducers from "./reducers";


import App from './App';
import useStyles from "./styles";
import {getPosts} from "./actions/posts";
import {getSets} from "./actions/sets";
import {AppBar, Container, Grid, Grow, Typography} from "@material-ui/core";
import memories from "./images/memories.png";
import Posts from "./components/Posts/Posts";
import Sets from "./components/Sets/Sets";
import Form from "./components/Form/Form";

function LearnSS() {
    const  classes = useStyles();
    const dispatch = useDispatch();

    useEffect(()=> {
        dispatch(getPosts());
        dispatch(getSets());
    },[dispatch]);

    return (
        <Container maxidth={"lg"}>
            <div>
                <h1>
                    Learn about StudyStream Here!
                </h1>
            </div>,
            <AppBar className={classes.appBar} position = "static" color ="inherit">
                <Typography className={classes.heading} variant="h2" aligin="center">StudyStream</Typography>
                <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>
            </AppBar>
            <Grow in>
                <Grid container justify="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={7}>
                        <Posts/>
                    </Grid>
                    <Grid item xs={12} sm={7}>
                        <Sets/>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <Form/>
                    </Grid>
                </Grid>
            </Grow>
        </Container>
    );
}

export default LearnSS;
