import React, {useEffect} from 'react';
// import {Container, AppBar, Typography, Grow, Grid, Switch, Button, Tab, Backdrop} from '@material-ui/core';
import {Container, AppBar, Typography, Grow, Grid} from '@material-ui/core';
import {Provider, useDispatch} from "react-redux";

import {getSets} from "./actions/sets";
import {getPosts} from "./actions/posts";

import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import Sets from './components/Sets/Sets';

import memories from './images/memories.png';
import useStyles from './styles';

// import './App.css';

//for multipage
import LearnSS from "./LearnSS"; //sample for extra page
import Home from "./Home"; //sample for extra page
import {useRoutes} from "react-router";
import ReactDOM from 'react-dom';
import { BrowserRouter,Routes, Route} from "react-router-dom";

const App = () => {
    const  classes = useStyles();
    const dispatch = useDispatch();

    useEffect(()=> {
        dispatch(getPosts());
        dispatch(getSets());
    },[dispatch]);

    return (
        <BrowserRouter>
            <Routes>
                <Posts/>
                <Route path="/">
                    <Posts/>
                </Route>
                <Route exact path="/posts">
                    <Posts/>
                </Route>
                <Route exact path="/sets">
                    <Sets/>
                </Route>
            </Routes>
        </BrowserRouter>,
            //^^ What is up w/ Routing??
            <LearnSS/>
);
}
export default App;
