import post from "../components/Posts/Post/Post";

export default (posts = [],action)=> {
  switch (action.type) {
    case 'FETCH_ALL':
      return action.payload;
    case 'CREATE':
      return [...post,action.payload];
    default:
      return posts;
  }
}
