import React, {useEffect} from 'react';
import ReactDOM from 'react-dom';
import {Provider, useDispatch} from 'react-redux';
import {createStore,applyMiddleware,compose} from 'redux';
import thunk from 'redux-thunk';
import reducers from "./reducers";

import {BrowserRouter, Link} from "react-router-dom"; //for multipage

import App from './App';
import {AppBar, Button, Container, Grid, Grow, Typography} from "@material-ui/core";
import Posts from "./components/Posts/Posts";
import Sets from "./components/Sets/Sets";
import Form from "./components/Form/Form";
import memories from "./images/memories.png";
import useStyles from "./styles";
import {getPosts} from "./actions/posts";
import {getSets} from "./actions/sets";
import LearnSS from "./LearnSS";
import UploadLecture from "./UploadLecture";


function Home() {

    const  classes = useStyles();
    const dispatch = useDispatch();

    useEffect(()=> {
        dispatch(getPosts());
        dispatch(getSets());
    },[dispatch]);

    return (
        <Container maxidth={"lg"}>
                <Grid container justify="left" alignItems="stretch" spacing={3}>
                <button type = "submit" name = "learn" value = "myimage" onClick={UploadLecture}>
                    <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>
                {/*<img src="https://www.tutorialspoint.com/latest/inter-process-communication.png " />*/}
            </button>

            <AppBar className={classes.appBar} position = "static" color ="inherit">
                <Typography className={classes.heading} variant="h2" aligin="center">StudyStream</Typography>
                {/*<img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>*/}
            </AppBar>
            <button type = "submit" name = "learn" value = "myimage" onClick={LearnSS}>
                <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>
                {/*<img src="https://www.tutorialspoint.com/latest/inter-process-communication.png " />*/}
            </button>
                </Grid>
            {/*//make back <Button className={classes.buttonSubmit} variant="contained" color="primary" size="small" type="submit" fullWidth>Submit Lecture</Button>*/}
            <Grow in>
                <Grid container justify="space-between" alignItems="stretch" spacing={3}>
                    <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>
                    <Grid item xs={12} sm={7}>
                            <Posts/>
                        </Grid>

                    <img className={classes.image} src = {memories} alt = "StudyStream Logo" height="60"/>
                    <Grid item xs={12} sm={7}>
                            <Sets/>
                        </Grid>

                        <Grid item xs={12} sm={7}>
                            <Form/>
                        </Grid>
                    </Grid>
            </Grow>
        </Container>
);
}
export default Home;
